    function changeSelect(i,val){
        data.slider[i-1].id = val;
    }

    $("#sliderpost").click(function(){
        //console.log(data);
        for(var i=0;i< data.slider.length;i++){
            if(data.slider[i].pic != null && data.slider[i].id == null){
