                type: "warning",
                msg: "您还没传图片",
                position: "center",
                width:"all",
                height:100,
                opacity: 1
            });
            return false;
        }
        $.post("<?php Yii::app()->request->baseUrl; ?>/Weixin/Templateslidersave",{template_id:template,site_id:site,sliderdata:data.slider},function(data){
            if(data.code == 0){
                notif({
                    type: "success",
                    msg: data.msg,
                    position: "center",
                    width:"all",
                    height:100,
                    opacity: 1
                });
                $('#slidereditdiv').hide();
            }else{
                notif({
                    type: "error",
                    msg: data.msg,
                    position: "center",
                    width:"all",
                    height:100,
                    opacity: 1
                });
                return false;
            }
        },'json');

    });

