               type: "warning",
               msg: "请选择图片",
               position: "center",
               width:"all",
               height:100,
               opacity: 1
           });
           return false;
       }

        if(!img_nav || typeof(img_nav) == undefined || (old_title == nav_title) ){
            if(img.length != 0){
                $('#naveditdiv').hide();
                return true;
            }else{
                notif({
                    type: "warning",
                    msg: "请选择图片",
                    position: "center",
                    width:"all",
                    height:100,
                    opacity: 1
                });
                return false;
            }
        }


    });

